mapper.select:
select id,userId,cpName as companyName,cpAddress as companyAddress,licenseNum,licenseImg,agencyNum,agencyImg,taxNum,taxImg,corporate,cpPhone as companyPhone,cpWebsite as companyWebsite,plName,
plType,plCardNum,plPositiveImg,plSideImg,plHidnumber,passport,type
from d_user_company where userId = #{0} and type=#{1}

select id,userId,cpName as companyName,cpAddress as companyAddress,licenseNum,licenseImg,agencyNum,agencyImg,taxNum,taxImg,corporate,cpPhone as companyPhone,cpWebsite as companyWebsite,plName,
plType,plCardNum,plPositiveImg,plSideImg,plHidnumber,passport,type
from d_user_company where id = #{id}

mapper.update:
UPDATE d_user_company


        plName = #{plName}


        ,plType = #{plType}


        ,plCardNum = #{plCardNum}


        ,plPositiveImg = #{plPositiveImg}


        ,plSideImg = #{plSideImg}


        ,plHidnumber = #{plHidnumber}


        ,passport = #{passport}


WHERE userId = #{userId} and type=1
